import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link, useHistory } from 'react-router-dom'
import { post, getTemperament } from './../../redux/actions/index';


export default function Create() {
  const dispatch = useDispatch();

  const history = useHistory();

  const temperamet = useSelector((state) => state.temps);
  //mi estado local
  const [errors, setErrors] = useState({});

  const [input, setInput] = useState({
    name: '',
    minHeight: '',
    maxHeight: '',
    minWeight: '',
    maxWeight: '',
    minLifeExp: '',
    maxLifeExp: '',
    img: '',
    temperament: [],
  })

  function handleChange(e) {
    setInput({
      ...input,
      [e.target.name]: e.target.value,
    })
    setErrors(validate({
      ...input,
      [e.target.name]: e.target.value,
    }))
  }

  function handleSelect(e) {
    setInput({
      ...input,
      temperament: [...input.temperament, e.target.value],
    })
    console.log(input);
  }

  function handleSubmit(e) {
    e.preventDefault();
    console.log(input);
    dispatch(post(input));
    alert('New dog breed Saved');
    setInput({
      name: '',
      minHeight: '',
      maxHeight: '',
      minWeight: '',
      maxWeight: '',
      minLifeExp: '',
      maxLifeExp: '',
      img: '',
      temperament: [],
    })
    history.push('/home');
  }

  function handleDelete(el) {
    setInput({
      ...input,
      temperament: input.temperament.filter(e => e !== el),
    })
  }

  useEffect(() => {
    dispatch(getTemperament());
  }, [dispatch])


  return (
    <div>
      <Link to="/home"><button title="return home and you will lose anything written there so far that is not saved ">Home</button></Link>
      <h1>Add the details for the new breed!</h1>

      <form onSubmit={e => handleSubmit(e)}>
        <div>
          <label>Breed Name:</label>
          <input type="text" name="name" value={input.name} onChange={e => handleChange(e)} placeholder='Breed...' title="MAX 50 alphanumeric characters, on English keyboard, without special characters like @" />
          {errors.name && (<p /*className=style.errors */>{errors.name}</p>)}
        </div>
        <div>
          <label>Min Height:</label>
          <input type="number" name="minHeight" value={input.minHeight} onChange={e => handleChange(e)} placeholder="add min height" title="most be a number max 5 character" />
          <label>cm</label>
          {errors.minHeight && (<p /*className=style.errors */>{errors.minHeight}</p>)}
        </div>
        <div>
          <label>Max Height:</label>
          <input type="number" name="maxHeight" value={input.maxHeight} onChange={e => handleChange(e)} placeholder="add max height" title="most be a number max 5 character" />
          <label>cm</label>
          {errors.maxHeight && (<p /*className=style.errors */>{errors.maxHeight}</p>)}
        </div>
        <div>
          <label>Min Weight:</label>
          <input type="number" name="minWeight" value={input.minWeight} onChange={e => handleChange(e)} placeholder="add min weight" title="most be a number max 5 character" />
          <label>kg</label>
          {errors.minWeight && (<p /*className=style.errors */>{errors.minWeight}</p>)}
        </div>
        <div>
          <label>Max Weight:</label>
          <input type="number" name="maxWeight" value={input.maxWeight} onChange={e => handleChange(e)} placeholder="add max Weight" title="most be a number max 5 character" />
          <label>kg</label>
          {errors.maxWeight && (<p /*className=style.errors */>{errors.maxWeight}</p>)}
        </div>
        <div>
          <label>Min Life Exp:</label>
          <input type="number" name="minLifeExp" value={input.minLifeExp} onChange={e => handleChange(e)} placeholder="add min number of years" title="most be a number max 5 character" />
          <label>years</label>
          {errors.minLifeExp && (<p /*className=style.errors */>{errors.minLifeExp}</p>)}
        </div>
        <div>
          <label>Max Life Exp:</label>
          <input type="number" name="maxLifeExp" value={input.maxLifeExp} onChange={e => handleChange(e)} placeholder="add max number of years" title="most be a number max 5 character" />
          <label>years</label>
          {errors.maxLifeExp && (<p /*className=style.errors */>{errors.maxLifeExp}</p>)}
        </div>
        <div>
          <label>Image:</label>
          <input type="text" name="img" value={input.img} onChange={e => handleChange(e)} placeholder="add image url" title="Remember img most be a url" />
          {errors.img && (<p /*className=style.errors */>{errors.img}</p>)}
        </div>
        <div>
          <label>Select one or more Temperaments:</label>
          <select onChange={e => handleSelect(e)} title="Select as many as you need" >
            {
              temperamet.map((t =>
                <option key={t.id} value={t.name} title="Select one or more">{t.name} </option>))
            }
            
          </select>
          <ul>
            {/* <li>{input.temperament.map(e => e + ", ")}</li> */}
            <li>{input.temperament.map((e) => (<div>{e} <button type="button" title="doble Click para borrar" key={e.id} onDoubleClick={() => handleDelete(e)}>'X'</button ></div>))}</li>
          </ul>
        </div>
        <div>
          <button type="submit" title="Save on DataBase and take you back home"
            disabled={
              input.temperament.length === 0 ||!input.name||!input.minHeight||!input.minWeight ? true : false
            }>
            Add Breed
          </button>
        </div>
      </form>
    </div>
  )
}
//

function validate(input) {
  let errors = {};
  if (!input.name || input.name.length > 50 || !input.name.match(/^[a-zA-Z0-9]+$/)) {
    errors.name = "Name is required and most be MAX 50 alphanumeric characters, on English keyboard, without special characters like @";
  }
  if (!input.minHeight || !input.maxHeight || input.minHeight > input.maxHeight||input.minHeight < 0||input.maxHeight.length>3) {
    errors.minHeight = "Height is required and must be less than Max Height and must be a number max 3 character";
  }
  if (!input.minWeight || !input.maxWeight || input.minWeight > input.maxWeight||input.minWeight < 0 ||input.maxWeight.length>3) {
    errors.minWeight = "Weight is required and must be less than Max Weight and must be a number max 3 character";
  }
  if (!input.minLifeExp || !input.maxLifeExp || input.minLifeExp > input.maxLifeExp||input.minLifeExp < 0|| input.maxLifeExp.length>2) {
    errors.minLifeExp = "Life Exp is required and must be less than Max Life Exp and  must be a number max 2 character";
  }
  if (!input.img||!input.img.match(/^(http|https):\/\/[^ "]+$/)) {
    errors.img = "Image is required but not obligatory & most be a url"
  }
  return errors;
}
