import React from 'react'
import { useParams } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux';
import { getDogDetail, clearPage } from '../../redux/actions/index'
import { useEffect } from 'react';

export default function Detail() {
  const { id } = useParams();
  const dispatch = useDispatch();
  //const{dogy}= useSelector(state =>state);
  const dogy = useSelector(state => state.detail);
  console.log(dogy);
  //Recivo mis 2 parametror al montar el componente
  useEffect(() => {
    dispatch(getDogDetail(id));
  }, [dispatch, id])
  return (
    <div>
      {dogy ? (
        <div>
          <h1>{dogy.name}</h1>
          <img src={dogy.img} alt={dogy.name} />
          <p key={dogy.id}>{dogy.temperament.map(e => e)}</p>
          <p>Height: {dogy.minHeight}-{dogy.maxHeight}cm</p>
          <p>Weight: {dogy.minWeight}-{dogy.maxWeight}Kg</p>
          <p>life expectancy: {dog.minLifeExp} - {dog.minLifeExp} Years</p>
          <button onClick={() => dispatch(clearPage())}>Back Home</button>
        </div>
      ) : (
        <h3>"No se encontr√≥ esa raza"</h3>
      )}
    </div>
  )
}
