//import { GET_DOGS } from './../actions/index';
import { GET_DOGS, CLEAR_PAGE, SEARCH_BREEDS, GET_TEMPERAMENT,FILTER_BY_CREATED } from "../actions/actionsTypes";
import { TEMPER } from './../actions/index';
const initialState = {
    dogs: [],
    allDogs: [],
    temps: [],
    detail: [],
}

export default function reducer(state = initialState, action) {
    switch (action.type) {
        case GET_DOGS:
            return {
                ...state,
                dogs: action.payload,
                allDogs: action.payload,//Copia q no modifico para el filtro
            }
        case CLEAR_PAGE:
            return {
                ...state,
                dogDetail: {} //estodo inicial de dogDetail
            }
        case SEARCH_BREEDS:
            return {
                ...state,
                searchDogs: action.payload
            }
        case GET_TEMPERAMENT:
            return {
                ...state,
                temps: action.payload
            }
        case FILTER:
            const allDogs = state.allDogs;
            //const temperFilter = allDogs.filter((e) => e.temps && e.temps.includes(action.payload))
            const temperFilter = action.payload === 'All' ? allDogs : allDogs.filter(e => e.state === action.payload)
            return {
                ...state,
                dogs: temperFilter
            }
        case FILTER_BY_CREATED:
            
            const createdFilter = action.payload === 'DB' ? state.allDogs.filter(i => i.createdInDB) : state.allDogs.filter(i => !i.createdInDB)
            return {
                ...state,
                dogs: action.payload === 'All' ? state.allDogs : createdFilter
            }


        default:
            return state;

    }
}
