//import { GET_DOGS } from './../actions/index';
import { GET_DOGS, CLEAR_PAGE, SEARCH_BREEDS, GET_TEMPERAMENT, FILTER_TEMPERAMENT } from "../actions/actionsTypes";
const initialState = {
    dogs: [],
    searchDogs: [],
    temps: [],
    tempsFilter
}

export default function reducer(state = initialState, action) {
    switch (action.type) {
        case GET_DOGS:
            return {
                ...state,
                dogs: action.payload
            }
        case CLEAR_PAGE:
            return {
                ...state,
                dogDetail: {} //estodo inicial de dogDetail
            }
        case SEARCH_BREEDS:
            return {
                ...state,
                searchDogs: action.payload
            }
        case GET_TEMPERAMENT:
            return {
                ...state,
                temps: action.payload
            }
        case FILTER_TEMPERAMENT:
            const allTemp = state.allDogs;
            const TempFiltered = [];
            if (action.payload === 'All') {TempFiltered = allTemp} else {
                for (let i = 0; i < allTemp.length; i++) {
                    if (allTemp[i].temperament) {
                        var temp = allTemp[i].temperament.toLowerCase();
                        if (temp.includes(action.payload)) {
                            TempFiltered.push(allTemp[i]);
                        }
                    }
                }
            }
            return {
                ...state,
                dogsFiltered: TempFiltered
            }
        default:
            return state;

    }
}
