import { STATUS } from './../actions/index';
const initialState = {
    dogs: [],
    allDogs: [],
    temps: [],
    detail: [],
}

export default function reducer(state = initialState, action) {
    switch (action.type) {
        case GET_DOGS:
            return {
                ...state,
                dogs: action.payload,
                allDogs: action.payload,//Copia q no modifico para el filtro
            }
        case CLEAR_PAGE:
            return {
                ...state,
                dogDetail: {} //estodo inicial de dogDetail
            }
        case SEARCH_BREEDS:
            return {
                ...state,
                searchDogs: action.payload
            }
        case GET_TEMPERAMENT:
            return {
                ...state,
                temps: action.payload
            }
        case TEMPER:
            const allDogs = state.allDogs;
            const temperFilter = allDogs.filter((d) => d.temps && d.temps.includes(action.payload))
            //const temperFilter = action.payload === 'All' ? allDogs : allDogs.filter(e => e.state === action.payload)
            return {
                ...state,
                dogs: temperFilter
            }  
        
        default:
            return state;

    }
}
