import React from 'react'
import { useParams } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux';
import { getDogsDetail, clearPage } from '../../redux/actions/index'
import { useEffect } from 'react';

export default function Detail() {
  const { id } = useParams();
  const dispatch = useDispatch();
  //const{detail}= useSelector(state =>state);
  const dog = useSelector(state => state.detail);
  console.log(dog);
  //Recivo mis 2 parametror al montar el componente
  useEffect(() => {
    dispatch(getDogsDetail(id));
  }, [dispatch, id])
  return (
    <div>
      {dog ? (
        <div>
      <h1>{dog.name}</h1>
      <img src={dog.img} alt={dog.name} />
      <p key={dog.id}>{dog.temperament.map(e=>e)}</p>
      <p>Height: {dog.minHeight}-{dog.maxHeight}cm</p>
      <p>Weight: {dog.minWeight}-{dog.maxWeight}Kg</p>
      <p>life expectancy: {dog.minLifeExp} - {dog.minLifeExp} Years</p>
      <button onClick={() => dispatch(clearPage())}>Back Home</button>
      </div>
      ) : (
        <h3>"No se encontr√≥ esa raza"</h3>
      )}
    </div>
  )
}
