export function getDogDetail(id) {
  return function (dispatch) {
    return axios.get(`/api/dogs/${id}`)//`http://localhost:3001/dogs/${id}
      .then(response => {
        dispatch({
          type: GET_DOGS_DETAIL,
          payload: response.data
        })
      })
      .catch(error => {
        throw error;
      });
  }
};
//
export const clearPage = () => {
  return {
    type: CLEAR_PAGE,
  };
};
